{
  if (VoxelFunc::IsReduction()) _foreachnonaryvoxelfunction_must_not_be_reduction();
  ParallelForEachVoxel(attr, *im1, *im2, *im3, *im4, *im5, *im6, *im7, *im8, *im9, vf);
}

