// -----------------------------------------------------------------------------
template <class T1, class T2, class T3, class T4, class T5, class T6, class T7, class T8, class T9, class VoxelFunc>
void ForEachScalar(GenericImage<T1> *im1, GenericImage<T2> *im2, GenericImage<T3> *im3, GenericImage<T4> *im4, GenericImage<T5> *im5, GenericImage<T6> *im6, GenericImage<T7> *im7, GenericImage<T8> *im8, GenericImage<T9> *im9, VoxelFunc &vf)
{
  NonaryForEachVoxelBody<T1, T2, T3, T4, T5, T6, T7, T8, T9, VoxelFunc> body(*im1, *im2, *im3, *im4, *im5, *im6, *im7, *im8, *im9, vf);
  blocked_range<int> re(0, im9->GetNumberOfVoxels());
  body(re);
  vf.join(body._VoxelFunc);
}

// -----------------------------------------------------------------------------
template <class T1, class T2, class T3, class T4, class T5, class T6, class T7, class T8, class T9, class VoxelFunc>
void ForEachScalar(VoxelFunc vf, GenericImage<T1> *im1, GenericImage<T2> *im2, GenericImage<T3> *im3, GenericImage<T4> *im4, GenericImage<T5> *im5, GenericImage<T6> *im6, GenericImage<T7> *im7, GenericImage<T8> *im8, GenericImage<T9> *im9)
{
  if (VoxelFunc::IsReduction()) _foreachnonaryvoxelfunction_must_not_be_reduction();
  ForEachScalar(*im1, *im2, *im3, *im4, *im5, *im6, *im7, *im8, *im9, vf);
}

